import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

class UserSignUp extends Component {

  constructor(props) {
    super(props);
    this.state = {
      firstName: '',
      lastName: '',
      emailAddress: '',
      password: '',
      confirmPassword: '',
      validationErrors: [],
      emailInUseError: '',
      passwordMatchError: '',
      matchingPasswords: true,
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit = (e) => {
    const { password, confirmPassword } = this.state 
    e.preventDefault();
    if(password !== confirmPassword) {
      this.setState ({ matchingPasswords: true })
      this.handleSignUp();
      //   passwordMatchError: "Error - Confirm password field doesn't match password", 
      //   validationErrors: [], 
      //   emailInUseError: ''
      // })
    } else {
      this.setState ({ matchingPasswords: false })
    }
  }

  handleSignUp = (e) => {
    const { firstName, lastName, emailAddress, password, confirmPassword } = this.state;
    axios({
      method: 'post',
      url: 'http://localhost:5000/api/users/',
      data: {
        firstName,
        lastName,
        emailAddress,
        password,
        confirmPassword,
      }
    })
    .then(res => {
      if (res.status === 201) {
        alert("Account Created!");
        this.props.history.push("/signin");
      } else {
        throw new Error();
      }
    })
    .catch(err => {
      if (err.response.data.message) {
        this.setState ({ emailInUseError: err.response.data.message })
        console.log(err.response.data.message);
      } else {
        this.setState({ validationErrors: err.response.data.errors })
      }
    });
  }

  // Handle changes to the form inputs
  handleChange = (e) => {
    this.setState({
      [ e.target.name ] : e.target.value 
    });
  }

  render() {

    const { validationErrors, emailInUseError, passwordMatchError } = this.state

    // const validationList = validationErrors.map((err) =>
    //   <li key={err.toString()}>{err}</li>
    // );

    return (
      <div className="bounds">
        <div className="grid-33 centered signin">
          <h1>Sign Up</h1>
          <div>
            <div className="validation-errors">
              <ul>
                { validationErrors ? (
                  validationErrors.map((err) =>
                    <li key={err.toString()}>{err}</li>
                  )
                ) :''}
                {emailInUseError ? (
                  <li>{emailInUseError}</li>
                ) : ''}
                {passwordMatchError ? (
                  <li>{passwordMatchError}</li>
                ) : ''}
              </ul>
            </div>
            <form onSubmit={this.handleSubmit}>
              <div>
                <input 
                  id="firstName" 
                  name="firstName" 
                  type="text"
                  className=""
                  placeholder="First Name"
                  value={this.state.firstName}
                  onChange={e => this.handleChange(e)}
                />
              </div>
              <div>
                <input 
                  id="lastName" 
                  name="lastName" 
                  type="text"
                  className=""
                  placeholder="Last Name"
                  value={this.state.lastName}
                  onChange={e => this.handleChange(e)}
                />
              </div>
              <div>
                <input 
                  id="emailAddress" 
                  name="emailAddress" 
                  type="email"
                  className=""
                  placeholder="Email Address"
                  value={this.state.emailAddress}
                  onChange={e => this.handleChange(e)}
                />
              </div>
              <div>
                <input 
                  id="password"
                  name="password"
                  type="password"
                  className=""
                  placeholder="Password"
                  value={this.state.password}
                  onChange={e => this.handleChange(e)}
                />
              </div>
              <div>
                <input 
                  id="confirmPassword"
                  name="confirmPassword"
                  type="password"
                  className=""
                  placeholder="Confirm Password"
                  value={this.state.confirmPassword}
                  onChange={e => this.handleChange(e)}
                />
              </div>
              <div className="grid-100 pad-bottom">
                <button className="button" type="sumbit">Sign In</button>
                <Link className="button button-secondary" to="/">Cancel</Link>
              </div>
            </form>
            <p>&nbsp;</p>
            <p>Already have a user account?
              <Link to="/signin"> Click here </Link>
              to sign in!
            </p>
          </div>
        </div>
      </div>
    )
  }
}

export default UserSignUp;
