import React, { Component } from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import axios from 'axios';

import Header from './components/Header';
import Courses from './components/Courses';
import CourseDetail from './components/CourseDetail';
import UserSignIn from './components/UserSignIn';
import UserSignUp from './components/UserSignUp';
import CreateCourse from './components/CreateCourse';
import UpdateCourse from './components/UpdateCourse';
import NotFound from './components/NotFound';
import Error from './components/Error';
import Forbidden from './components/Fobidden';

class App extends Component {

  state = {
    authUserData: {},
    validationErrors: '',
  }

  handleSignIn(e, emailAddress, password, props) {
    if (e) {
      e.preventDefault();
    }
    axios.get('http://localhost:5000/api/users',
      {
        auth: {
          username: emailAddress,
          password: password
        }
     })
     .then(res => {
       if (res.status === 200) {
        const user = res.data;
        const fullName = user.firstName + ' ' + user.lastName;

        localStorage.setItem('id', user.id);
        localStorage.setItem('emailAddress', emailAddress);
        localStorage.setItem('password', password);
        localStorage.setItem('fullName', fullName);

        this.setState({
          validationErrors: ''
        });
        const { history, location } = props;
        const path = location.state ? location.state.prevLocation : '/courses';
        history.push(path);
       }
     })
     .catch( err => {
      if (err.response.status === 400) {
        this.setState({
          validationErrors: err.response.data.message
        });
      } else if (err.response.status === 500) {
        console.error('Error fetching and parsing data', err);
        this.props.history.push('/error');
      }
    });
  }

  handleSignOut() {
    localStorage.clear();
    this.setState({
      validationErrors: ''
    });
    this.props.history.push('/');
  }

  render() {
    return (
      <BrowserRouter>
        <div>
          <Header />
          <Switch>
            <Route exact path="/" component={Courses} />
            <Route exact path="/courses/:id" component={CourseDetail} />
            <Route path="/signin" render={ () => <UserSignIn /> } />
            <Route exact path="/signup" component={UserSignUp} />
            <Route exact path="/courses/create" component={CreateCourse} />
            <Route exact path="/courses/:id/update" component={UpdateCourse} />
            <Route exact path="/fobidden" component={Forbidden} />
            <Route exact path="/notfound" component={NotFound} />
            <Route exact path="/error" component={Error} />
            <Route render={() => <NotFound /> } />
          </Switch>
        </div>
      </BrowserRouter>
    )
  }
}

export default App;
