import React, { Component } from 'react';
import { Link } from 'react-router-dom';

class UserSignIn extends Component {

  constructor(props) {
    super(props);
    this.state = {
      emailAddress: '',
      password: '',
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  // Handle form submit
  handleSubmit = e => {
    const { emailAddress, password } = this.state;
    e.preventDefault();
    let loginDetails = {"emailAddress": emailAddress, "password": password}
    this.props.signIn(loginDetails)
  };

  // Handle changes to the form inputs
  handleChange = e => {
    this.setState({
      [ e.target.name ] : e.target.value 
    });
  }


  // // handleSubmit = (e, signIn) => {
  // //   const { emailAddress, password } = this.state;
  // //   e.preventDefault();
  // //   signIn(emailAddress, password, this.props);
  // // }


  render() {
    // const { authenticationError } = this.state;
    // console.log(authenticationError)
    return (
        <div className="bounds">
          <div className="grid-33 centered signin">
            <h1>Sign In</h1>
            <div>
            {console.log(authenticationError)}
              { this.props.authenticationError &&  (
              <>
                <h2 className="validation--errors--label">Oops!</h2>
                <div className="validation-errors">
                 <p>{this.props.authenticationError}</p>
                </div>
              </>
              )}
              {/* {authenticationErrors ? (
                <div>
                  <h2 className="validation--errors--labels">Authentication error</h2>
                  <div className="validation-errors">
                    <ul>
                      <li>{authenticationErrors}</li>
                    </ul>
                  </div>
                </div>
              ):''} */}
              <form onSubmit={this.handleSubmit}>
                <div>
                  <input 
                    id="emailAddress" 
                    name="emailAddress" 
                    type="email"
                    className=""
                    placeholder="Email Address"
                    value={this.state.emailAddress}
                    onChange={e => this.handleChange(e)}
                  />
                </div>
                <div>
                  <input 
                    id="password"
                    name="password"
                    type="password"
                    className=""
                    placeholder="Password"
                    value={this.state.password}
                    onChange={e => this.handleChange(e)}
                  />
                </div>
                <div className="grid-100 pad-bottom">
                  <button className="button" type="sumbit">Sign In</button>
                  <Link className="button button-secondary" to="/">Cancel</Link>
                </div>
              </form>
              <p>&nbsp;</p>
              <p>Don't have a user account?
                <Link to="/signup"> Click here </Link>
                to sign up!
              </p>
            </div>
          </div>
        </div>
      )
  }
}

export default UserSignIn;
