import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import ReactMarkdown from 'react-markdown';

class CourseDetail extends Component {
  state = {
    course: {},
    id: this.props.match.params.id,
    fullName: '',
  };

  componentDidMount() {
    this.getCourse();
  }

  /***
  * Fetch the list of courses from the API using axios
  ***/
  getCourse = () => {
    const { id } = this.state;
    axios.get(`http://localhost:5000/api/courses/${id}`)
      .then(res => {
        console.log(res.data);
        this.setState({
          courses: res.data,
          fullName: `${res.data.User.firstName} ${res.data.User.lastName}`,
        });
        // console.log(courses);
      })
      //Catch any errors and log it to the console
      .catch(err => {
        console.log('Error fetching and parsing data', err);
      });
  }

  render () {
    const { id, course } = this.state;
    // const { course, id, title, name, description, estimatedTime, materialsNeeded, fullName } = this.state;
    // console.log({this.state.data.course.title})
    return (
      <div>
        <div className="actions--bar">
          <div className="bounds">
            <div className="grid-100">
              <span>
                <Link className="button" to={`/courses/${id}/update`}>
                  Update Course
                </Link>
                <button className="button" to="/">
                  Delete Course
                </button>
              </span>
              <Link className="button button-secondary" to="/">
                Return to List
              </Link>
            </div>
          </div>
        </div>
        <div className="bounds course--detail">
          <div className="grid-66">
            <div className="course--header">
              <h4 className="course--label">Course</h4>
              <h3 className="course--title">{this.state.course.title}</h3>
              <p>By {this.state.fullName}</p>
            </div>
            <div className="course-description">
                <ReactMarkdown source={this.state.course.description} />
            </div>
          </div>
        </div>
      </div>
    )
  }

}

export default CourseDetail;
